'use client';
import { useState } from 'react';
import { AnimatedNumber } from '@/components/core/animated-number';
import { Minus, Plus } from 'lucide-react';

export function AnimatedNumberCounter() {
  const [value, setValue] = useState(1000);

  return (
    <div className='flex w-full items-center justify-center space-x-2 text-zinc-800 dark:text-zinc-50'>
      <button
        aria-label='Decrement'
        onClick={() => setValue((prev) => prev - 100)}
      >
        <Minus className='h-4 w-4' />
      </button>
      <AnimatedNumber
        className='inline-flex items-center font-mono text-2xl font-light'
        springOptions={{
          bounce: 0,
          duration: 1000,
        }}
        value={value}
      />
      <button
        aria-label='Increment'
        onClick={() => setValue((prev) => prev + 100)}
      >
        <Plus className='h-4 w-4' />
      </button>
    </div>
  );
}

'use client';
import { cn } from '@/lib/utils';
import { motion, SpringOptions, useSpring, useTransform } from 'motion/react';
import { useEffect } from 'react';

export type AnimatedNumberProps = {
  value: number;
  className?: string;
  springOptions?: SpringOptions;
  as?: React.ElementType;
};

export function AnimatedNumber({
  value,
  className,
  springOptions,
  as = 'span',
}: AnimatedNumberProps) {
  const MotionComponent = motion.create(as as keyof JSX.IntrinsicElements);

  const spring = useSpring(value, springOptions);
  const display = useTransform(spring, (current) =>
    Math.round(current).toLocaleString()
  );

  useEffect(() => {
    spring.set(value);
  }, [spring, value]);

  return (
    <MotionComponent className={cn('tabular-nums', className)}>
      {display}
    </MotionComponent>
  );
}
